export function TechLogos() {
  // Duplicate the technologies array to create seamless loop
  const duplicatedTechs = [...technologies, ...technologies];

  return (
    <div className="w-full overflow-hidden py-4 sm:py-6 md:py-8">
      <div className="relative">
        {/* Gradient overlays for fade effect */}
        <div className="absolute left-0 top-0 bottom-0 w-16 sm:w-24 md:w-32 bg-gradient-to-r from-gray-50 dark:from-[#1a2332] to-transparent z-10" />
        <div className="absolute right-0 top-0 bottom-0 w-16 sm:w-24 md:w-32 bg-gradient-to-l from-gray-50 dark:from-[#1a2332] to-transparent z-10" />
        
        {/* Scrolling container - Responsive logo sizing */}
        <motion.div
          className="flex gap-4 sm:gap-6 md:gap-8 lg:gap-12"
          animate={{
            x: [0, -(technologies.length * 140)],
          }}
          transition={{
            x: {
              repeat: Infinity,
              repeatType: "loop",
              duration: 40,
              ease: "linear",
            },
          }}
        >
          {duplicatedTechs.map((tech, index) => (
            <div
              key={`${tech.name}-${index}`}
              className="flex-shrink-0 w-24 h-16 sm:w-28 sm:h-18 md:w-32 md:h-20 flex flex-col items-center justify-center gap-1 sm:gap-2 bg-white dark:bg-gray-900/50 rounded-lg sm:rounded-xl border border-gray-200 dark:border-gray-800 p-2 sm:p-3 md:p-4 hover:border-blue-500 dark:hover:border-blue-500 transition-all hover:shadow-lg group"
            >
              <div className={`w-8 h-8 sm:w-10 sm:h-10 md:w-12 md:h-12 ${tech.color} transition-transform group-hover:scale-110`}>
                {tech.logo}
              </div>
              <span className="text-[10px] sm:text-xs text-gray-600 dark:text-gray-400 text-center whitespace-nowrap">
                {tech.name}
              </span>
            </div>
          ))}
        </motion.div>
      </div>
    </div>
  );
}
